*,
*:before,
*:after {
  box-sizing: inherit;
}

body {
  background: url("/static/img/background.png") no-repeat center center #fff;
  background-size: cover;
  box-sizing: border-box;
}

.container {
  max-width: 160rem;
  margin: auto;
  padding-left: 2rem;
  padding-right: 2rem;
}

.header {
  margin-bottom: 2rem;
  padding-top: 2rem;
  padding-bottom: 0.5rem;
  text-align: center;
  background: #fff;
}

textarea {
  min-height: 20rem;
  resize: none;
  background: #fff;
}

.text-center {
  text-align: center;
}

.button-large {
  font-size: 1.4rem;
  height: 4.5rem;
  line-height: 4.5rem;
  padding: 0 2rem;
  transition: height 0.3s;
}

.footer {
  padding: 2rem 2rem;
  background: #9b4dca;
  color: #fff;
}

.footer a {
  color: currentColor;
  text-decoration: underline;
}

.footer a:hover {
  color: #ccc;
}

@media screen and (min-width: 40rem) {
  .footer {
    padding: 2rem 10rem;
  }
}

.github-corner:hover .octo-arm {
  animation: octocat-wave 560ms ease-in-out;
}

@media (max-width: 500px) {
  .github-corner:hover .octo-arm {
    animation: none;
  }

  .github-corner .octo-arm {
    animation: octocat-wave 560ms ease-in-out;
  }
}

@keyframes octocat-wave {
  0%,
  100% {
    transform: rotate(0);
  }
  20%,
  60% {
    transform: rotate(-25deg);
  }
  40%,
  80% {
    transform: rotate(10deg);
  }
}


